var ajax=function(url,body,headers,type){this.url=url
    this.body=body
    this.headers=headers
    this.type=type
    ajax.prototype.execAjax=function(){this.result=$.ajax({url:this.url,type:this.type,contentType:'json',data:this.body,});return this}
    ajax.prototype.getResult=function(){return this.result}
    ajax.prototype.getDataFromForm=function(){var indexed_array={};$.map(this.body,function(n,i){indexed_array[n.name]=n.value});this.body=indexed_array;console.log(this.body);return this}}